package com.eden.enforcementService.clients;import com.eden.enforcementService.clients.dtos.ApiResponse;import com.eden.enforcementService.clients.dtos.InvoiceDto;import com.eden.enforcementService.common.request.InvoiceRequest;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.http.HttpHeaders;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestHeader;import javax.ws.rs.HeaderParam;@FeignClient(value = "invoice-service", path = "/invoices")public interface InvoiceFeignClient {    @PostMapping("/addInvoice")    ApiResponse<InvoiceDto> addInvoice(@RequestHeader(HttpHeaders.AUTHORIZATION) String authorizationHeader, @RequestBody InvoiceRequest invoiceRequest);}