package com.eden.enforcementService.common.request;import com.eden.enforcementService.util.Constants;import lombok.Builder;import lombok.Data;import javax.validation.constraints.NotNull;import java.time.LocalDate;import java.time.LocalTime;@Data@Builderpublic class WhiteListedDetailsRequest {    @NotNull(message = Constants.ErrorKeys.EMPTY_USER_NAME)    private String userName;    @NotNull(message = Constants.ErrorKeys.EMPTY_COUNTRY_NAME)    private String countryName;    @NotNull(message = Constants.ErrorKeys.EMPTY_FROM_DATE)    private LocalDate fromDate;    @NotNull(message = Constants.ErrorKeys.EMPTY_TO_DATE)    private LocalDate toDate;    @NotNull(message = Constants.ErrorKeys.EMPTY_FROM_TIME)    private LocalTime timeFrom;    @NotNull(message = Constants.ErrorKeys.EMPTY_TO_TIME)    private LocalTime timeTo;    private String description;}